def debug_info(func):
    """A decorator that prints the name and docstring of the function."""
    def wrapper(*args, **kwargs):
        print(f"Function name: {func.__name__}")
        print(f"Docstring: {func.__doc__}")
        return func(*args, **kwargs)
    return wrapper

@debug_info
def add_numbers(a, b):
    """Adds two numbers together."""
    return a + b

@debug_info
def multiply_numbers(a, b):
    """Multiplies two numbers together."""
    return a * b

# Calculate the sum of 1 and 2
result_add = add_numbers(1, 2)
print(f"The sum of 1 and 2 is {result_add}")

# Calculate the product of 4 and 5
result_multiply = multiply_numbers(4, 5)
print(f"The product of 4 and 5 is {result_multiply}")

# Intentional error: Trying to concatenate string and integer
print("The sum of 1 and 2 is " + result_add)
