def evaluate_expression(expr):
    # This function evaluates a mathematical expression
    try:
        result = eval(expr)
        print("The result of the expression is:", result)
    except Exception as e:
        print("There was an error evaluating the expression:", e)

def main():
    # Let's evaluate a simple math expression
    math_expression = "3 * (4 + 2)"
    evaluate_expression(math_expression)

    # Now let's evaluate a more complex expression
    complex_expression = "5 * (3 - (2 ** (1/2))"
    evaluate_expression(complex_expression)

if __name__ == "__main__":
    main()
