class Animal:
    def speak(self):
        raise NotImplementedError("Subclasses must implement this method")

class Dog(Animal):
    def speak(self):
        return "Woof!"

class Cat(Animal):
    def speak(self):
        return "Meow!"

class Duck(Animal):
    def speak(self):
        return "Quack!"

def animal_sound(animals):
    for animal in animals:
        print(animal.speak())

# Main program
animals = [Dog(), Cat(), Duck(), "Tiger"]
animal_sound(animals)
