class MirrorObject:
    def __init__(self, original):
        self.original = original

    def reflect_methods(self):
        methods = [method for method in dir(self.original) if callable(getattr(self.original, method))]
        for method in methods:
            setattr(self, method, self._create_reflection(method))

    def _create_reflection(self, method_name):
        def reflected_method(*args, **kwargs):
            return getattr(self.original, method_name)(*args, **kwargs)
        return reflected_method

# Example usage of MirrorObject
if __name__ == "__main__":
    original_list = [1, 2, 3, 4]
    mirrored_list = MirrorObject(original_list)
    mirrored_list.reflect_methods()

