import json

# Define a simple class to represent a user
class User:
    def __init__(self, name, age):
        self.name = name
        self.age = age

    # Method to serialize the user object to JSON
    def to_json(self):
        return json.dumps(self.__dict__)

    # Method to create a User object from a JSON string
    @staticmethod
    def from_json(json_str):
        data = json.loads(json_str)
        return User(data['name'], data['age'])

# Create a new user object
user = User("John Doe", 30)

# Serialize the user object to JSON
user_json = user.to_json()

# Print the serialized user
print("Serialized user:", user_json)

# Deserialize the JSON back to a User object
new_user = User.from_json(user_json)

# Print the deserialized user's details
print("Deserialized user:", new_user.name, new_user.age)

# Intentional Error: Attempt to serialize a non-serializable object (set)
def serialize_set():
    sample_set = {1, 2, 3}
    return json.dumps(sample_set)

# Call the function that causes an error
serialize_set()
