def convert_string_to_bytes(string_data):
    # This function converts a string to its bytes representation
    return bytes(string_data, 'utf-8')

def save_bytes_to_file(byte_data, file_name):
    # Saves the bytes data to a file
    with open(file_name, 'wb') as file:
        file.write(byte_data)

def read_bytes_from_file(file_name):
    # Reads bytes from a file
    with open(file_name, 'rb') as file:
        return file.read()

def main():
    # Convert string to bytes and save to file
    original_string = "Hello, world!"
    byte_data = convert_string_to_bytes(original_string)
    save_bytes_to_file(byte_data, 'output.txt')

    # Read bytes from file and print
    read_data = read_bytes_from_file('output.txt')
    print(read_data)

if __name__ == "__main__":
    main()
