# This program creates a list of prime numbers up to a given number

def is_prime(num):
    if num < 2:
        return False
    for i in range(2, num):
        if num % i == 0:
            return False
    return True

def generate_prime_numbers(limit):
    prime_numbers = []
    for number in range(limit):
        if is_prime(number):
            prime_numbers.appen(number)  # Intentional error: 'appen' should be 'append'
    return prime_numbers

# Generate prime numbers up to 100
primes_up_to_100 = generate_prime_numbers(100)
print("Prime numbers up to 100:", primes_up_to_100)
