def count_occurrences(word, letter):
    # This function counts the number of times a letter appears in a word
    count = 0
    for char in word:
        if char == letter:
            count += 1
    return count

# Let's count how many times the letter 'a' appears in the word 'banana'
word_to_check = 'banana'
letter_to_count = 'a'
occurrences = count_occurrences(word_to_check, letter_to_count)

print(f"The letter '{letter_to_count}' appears in '{word_to_check}' {occurrences} times.")

# Now let's count the occurrences of each letter in the word
def count_all_letters(word):
    letter_counts = {}
    for letter in word:
        if letter in letter_counts:
            letter_counts[letter] += 1
        else:
            letter_counts[letter] = 1
    return letter_counts

all_letter_counts = count_all_letters(word_to_check)

# Print the count for each letter
for letter, count in all_letter_counts.items():
    print(f"Letter '{letter}' appears {count} times.")

