def check_set_relationship(set_a, set_b):
    # Function to check if one set is a subset or superset of another set
    if set_a.issubset(set_b):
        print("Set A is a subset of Set B.")
    elif set_b.issubset(set_a):
        print("Set B is a subset of Set A.")
    if set_a.issuperset(set_b):
        print("Set A is a superset of Set B.")
    elif set_b.issuperset(set_a):
        print("Set B is a superset of Set A.")
    else:
        print("Neither set is a subset or superset of the other.")

