import json

# Define a function to merge two dictionaries
def merge_dictionaries(dict1, dict2):
    return {**dict1, **dict2}

# Define a function to convert dictionary to JSON and join with a delimiter
def convert_and_join(dict_data, delimiter):
    json_data = json.dumps(dict_data)
    return delimiter.join(json_data)

# Sample dictionaries
dict1 = {'key1': 'value1', 'key2': 'value2'}
dict2 = {'key3': 'value3'}

# Merge dictionaries
merged_dict = merge_dictionaries(dict1, dict2)

# Convert to JSON and join with a delimiter
result = convert_and_join(merged_dict, '-')

# Print the result
print(result)
