import re

def remove_special_characters(text):
    # This function removes special characters from a given string
    return re.sub(r'[^a-zA-Z0-9 ]', '', text)

def main():
    sample_text = "This is a sample text with some special characters! @#$%^&*()"
    cleaned_text = remove_special_characters(sample_text)
    print(f"Original: {sample_text}\nCleaned: {cleaned_text}")

