def fibonacci(n):
    # A function to return the nth Fibonacci number
    if n == 0:
        return 0
    elif n == 1:
        return 1
    else:
        return fibonacci(n - 1) + fibonacci(n - 2)

def pretty_print_fibonacci(n):
    # Print Fibonacci numbers in a pretty format
    for i in range(n):
        print(f"Fibonacci number {i} is {fibonacci(i)}")

def idiomatic_python_example():
    # Demonstrates idiomatic Python by using list comprehensions
    squares = [x**2 for x in range(10)]
    print("Squares of numbers from 0 to 9:", squares)

# Main function to demonstrate the functionality
if __name__ == "__main__":
    pretty_print_fibonacci(10)
