from flask import Flask

app = Flask(__name__)

# Function to find the factorial of a number
def find_factorial(num):
    if num == 1:
        return 1
    else:
        return num * find_factorial(num)

# Route to calculate factorial of 5
@app.route('/factorial')
def calculate_factorial():
    result = find_factorial(5)
    return f"The factorial of 5 is {result}"

if __name__ == '__main__':
    app.run(debug=True)
