from flask import Flask

app = Flask(__name__)

@app.route('/')
def hello_world():
    return 'Hello, World!'

@app.route('/format_date/<date>')
def format_date(date):
    try:
        # Assume date is in the format YYYYMMDD and convert it to YYYY-MM-DD
        formatted_date = "{}-{}-{}".format(date[:4], date[4:6], date[6:])
        return 'Formatted date: ' + formatted_date
    except IndexError:
        # If the date is not in the expected format, return an error message
        return 'Error: Date should be in YYYYMMDD format.'

# Note: The following line should be 'if __name__ == "__main__":'
# to ensure the Flask app only runs when executed directly.
if __name__ == "_main__":
    app.run(debug=True)
