def concatenate_items(item_list):
    # This function takes a list of strings and concatenates them into a single string.
    return ''.join(item_list)

def main():
    items_to_join = ["Python", "is", "fun", "!", "Let's", "code"]
    concatenated_items = concatenate_items(items_to_join)
    print(concatenated_items)

# Call the main function to concatenate items and display them
main()
